mapper.select:
SELECT /*com.prisca.user.mapper.ReserveMapper.selectResveManageList */ RESV.ROOM_NO AS roomNo ,RESV.KMC_CD AS kmcCd ,RESV.START_YMD AS startYmd ,RESV.END_YMD AS endYmd
,RESV_DATE as resvDate ,extract ('day' from TO_DATE(resv.start_ymd,'YYYYMMDD')) as startDay ,extract ('day' from TO_DATE(resv.end_ymd,'YYYYMMDD')) as endDay FROM KMC_RESV_MANAGE RESV WHERE 1=1
AND START_YMD <= #{endYmd}
AND END_YMD >= #{startYmd}
AND RESV.ROOM_NO = #{roomNo}
AND RESV.KMC_CD = #{mCd}
ORDER BY RESV.ROOM_NO

SELECT /*com.prisca.user.mapper.ReserveMapper.selectResveManageList */ RESV.ROOM_NO AS roomNo ,RESV.KMC_CD AS kmcCd ,RESV.START_YMD AS startYmd ,RESV.END_YMD AS endYmd
,RESV_DATE as resvDate ,extract ('day' from TO_DATE(resv.start_ymd,'YYYYMMDD')) as startDay ,extract ('day' from TO_DATE(resv.end_ymd,'YYYYMMDD')) as endDay FROM KMC_RESV_MANAGE RESV WHERE 1=1
AND START_YMD <= #{endYmd}
AND END_YMD >= #{startYmd}
AND RESV.ROOM_NO = #{roomNo}
AND RESV.KMC_CD = #{mCd}
ORDER BY RESV.ROOM_NO
